<!DOCTYPE html>\n<html>\n<head>\n    <title>Laporan Membership</title>\n    <style>\n        body {\n            font-family: Arial, sans-serif;\n            font-size: 12px;\n        }\n        .header {\n            text-align: center;\n            margin-bottom: 20px;\n        }\n        .period {\n            text-align: center;\n            margin-bottom: 20px;\n            font-style: italic;\n        }\n        table {\n            width: 100%;\n            border-collapse: collapse;\n            margin-bottom: 20px;\n        }\n        th, td {\n            border: 1px solid #ddd;\n            padding: 8px;\n            text-align: left;\n        }\n        th {\n            background-color: #f2f2f2;\n            font-weight: bold;\n        }\n        .text-center {\n            text-align: center;\n        }\n        .text-right {\n            text-align: right;\n        }\n        .badge {\n            padding: 2px 6px;\n            border-radius: 3px;\n            font-size: 10px;\n        }\n        .badge-success {\n            background-color: #28a745;\n            color: white;\n        }\n        .badge-danger {\n            background-color: #dc3545;\n            color: white;\n        }\n        .badge-warning {\n            background-color: #ffc107;\n            color: black;\n        }\n    </style>\n</head>\n<body>\n    <div class=\"header\">\n        <h2>Laporan Membership</h2>\n        <h3>Gym & POS System</h3>\n    </div>\n    \n    <div class=\"period\">\n        Periode: {{ $startDate->format('d/m/Y') }} - {{ $endDate->format('d/m/Y') }}\n    </div>\n    \n    <table>\n        <thead>\n            <tr>\n                <th>No</th>\n                <th>Member</th>\n                <th>Tipe</th>\n                <th>Tanggal Pembelian</th>\n                <th>Mulai</th>\n                <th>Selesai</th>\n                <th>Harga</th>\n                <th>Status</th>\n            </tr>\n        </thead>\n        <tbody>\n            @foreach($memberships as $index => $membership)\n            <tr>\n                <td class=\"text-center\">{{ $index + 1 }}</td>\n                <td>\n                    <strong>{{ $membership->member->name }}</strong><br>\n                    <small>{{ $membership->member->phone }}</small>\n                </td>\n                <td>{{ $membership->type }}</td>\n                <td class=\"text-center\">{{ $membership->created_at->format('d/m/Y') }}</td>\n                <td class=\"text-center\">{{ $membership->start_date->format('d/m/Y') }}</td>\n                <td class=\"text-center\">{{ $membership->end_date->format('d/m/Y') }}</td>\n                <td class=\"text-right\">Rp {{ number_format($membership->price, 0, ',', '.') }}</td>\n                <td class=\"text-center\">\n                    <span class=\"badge badge-{{ $membership->status === 'active' ? 'success' : 'danger' }}\">\n                        {{ ucfirst($membership->status) }}\n                    </span>\n                </td>\n            </tr>\n            @endforeach\n        </tbody>\n    </table>\n    \n    <div style=\"margin-top: 30px;\">\n        <p><strong>Total Membership:</strong> {{ $memberships->count() }}</p>\n        <p><strong>Total Pendapatan:</strong> Rp {{ number_format($memberships->sum('price'), 0, ',', '.') }}</p>\n    </div>\n    \n    <div style=\"margin-top: 50px; text-align: right;\">\n        <p>Dicetak pada: {{ now()->format('d/m/Y H:i') }}</p>\n    </div>\n</body>\n</html>